import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Image } from 'react-native';

const SignupScreen = ({ navigation }) => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [formValid, setFormValid] = useState(true);

  const handleSignUp = () => {
    // Perform validation checks
    if (
      firstName.trim() === '' ||
      lastName.trim() === '' ||
      email.trim() === '' ||
      password.trim() === '' ||
      confirmPassword.trim() === ''
    ) {
      setFormValid(false);
      return;
    }

    // Save user's information to the database
    const user = {
      firstName,
      lastName,
      email,
      password,
    };

    // Make an API call to save the user's information to the SQL database
    fetch('https://example.com/api/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(user),
    })
      .then(response => {
        // Handle the response from the API
        if (response.ok) {
          // User successfully registered, navigate to the login screen
          navigation.navigate('Login');
        } else {
          // Error occurred while saving user's information
          // Handle the error, display an error message, etc.
          console.log('Error:', response.statusText);
        }
      })
      .catch(error => {
        // Handle network errors
        console.log('Error:', error.message);
      });
  };

  return (
    <View style={styles.container}>
      <View style={styles.logoContainer}>
        <Image source={require('../img/logo.png')} style={styles.image} accessible={true} accessibilityLabel={'logo'} />
      </View>
      <View style={styles.formContainer}>
        <Text style={styles.label}>First Name</Text>
        <TextInput
          style={styles.input}
          placeholder="First Name"
          value={firstName}
          onChangeText={text => setFirstName(text)}
        />

        {/* Other input fields */}

        <TouchableOpacity style={styles.button} onPress={handleSignUp}>
          <Text style={styles.buttonText}>Sign Up</Text>
        </TouchableOpacity>

        {/* Form validation error */}
        {!formValid && (
          <Text style={styles.errorText}>Please fill in all the required fields.</Text>
        )}

        {/* Footer */}
        <View style={styles.footerRow}>
          <Text style={styles.agreeText}>
            By creating an account, I agree to Travelyâ€™s{' '}
            <Text style={styles.link}>Terms of Use</Text> and{' '}
            <Text style={styles.link}>Privacy Policy.</Text>
          </Text>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'white',
  },
  logoContainer: {
    alignItems: 'center',
    marginBottom: 40,
  },
  image: {
    width: 208,
    height: 41,
    borderRadius: 20,
  },
  formContainer: {
    width: '80%',
    paddingTop: 57,
  },
  label: {
    fontWeight: '600',
    fontSize: 14,
    marginBottom: 4,
  },
  input: {
    borderRadius: 15,
    backgroundColor: '#f1f1f1',
    width: 300,
    height: 40,
    padding: 10,
    marginBottom: 16,
  },
  button: {
    backgroundColor: '#ED203E',
    borderRadius: 15,
    width: 300,
    alignItems: 'center',
    padding: 10,
    marginBottom: 16,
    marginTop: 10,
  },
  buttonText: {
    color: 'white',
    fontWeight: '600',
    letterSpacing: 2,
    fontSize: 14,
  },
  errorText: {
    color: 'red',
    marginBottom: 16,
  },
  footerRow: {
    marginBottom: -60,
  },
  agreeText: {
    fontSize: 11,
    padding: 20,
    textAlign: 'center',
  },
  link: {
    color: '#ED203E',
  },
});

export default SignupScreen;
