import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  Button,
  TouchableOpacity,
  ScrollView,
} from 'react-native';

const TaskManager = () => {
  const [tasks, setTasks] = useState([]);

  const addTask = (taskName) => {
    const task = {
      name: taskName,
      status: 'Pending',
    };
    setTasks((prevTasks) => [...prevTasks, task]);
  };

  const updateTaskStatus = (index, status) => {
    setTasks((prevTasks) =>
      prevTasks.map((task, i) =>
        i === index ? { ...task, status: status } : task
      )
    );
  };

  const deleteTask = (index) => {
    setTasks((prevTasks) =>
      prevTasks.filter((_, i) => i !== index)
    );
  };

  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Task Manager</Text>
      <View style={styles.taskInputContainer}>
        <TextInput
          style={styles.taskInput}
          placeholder="Enter task name"
          onChangeText={(text) => setTaskName(text)}
        />
        <Button
          title="Add Task"
          onPress={() => addTask(taskName)}
        />
      </View>
      <ScrollView style={styles.tasksContainer}>
        {tasks.map((task, index) => (
          <View style={styles.taskRow} key={index}>
            <Text style={styles.taskNumber}>{index + 1}</Text>
            <Text style={styles.taskName}>{task.name}</Text>
            <View style={styles.taskStatusDropdown}>
              <TouchableOpacity style={styles.dropdownButton}>
                <Text>{task.status}</Text>
              </TouchableOpacity>
              <View style={styles.dropdownMenu}>
                <TouchableOpacity
                  style={styles.dropdownItem}
                  onPress={() => updateTaskStatus(index, 'Pending')}
                >
                  <Text>Pending</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={styles.dropdownItem}
                  onPress={() => updateTaskStatus(index, 'In Progress')}
                >
                  <Text>In Progress</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={styles.dropdownItem}
                  onPress={() => updateTaskStatus(index, 'Completed')}
                >
                  <Text>Completed</Text>
                </TouchableOpacity>
              </View>
            </View>
            <View style={styles.taskButtons}>
              <Button
                title="Edit"
                onPress={() => handleEditTask(index)}
              />
              <Button
                title="Delete"
                onPress={() => deleteTask(index)}
              />
            </View>
          </View>
        ))}
      </ScrollView>
    </View>
  );
};

const styles = {
  container: {
    flex: 1,
    backgroundColor: '#202020',
    padding: 20,
  },
  heading: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 20,
  },
  taskInputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
  },
  taskInput: {
    flex: 1,
    height: 40,
    backgroundColor: '#252525',
    borderRadius: 8,
    color: 'white',
    paddingHorizontal: 10,
  },
  tasksContainer: {
    flex: 1,
  },
  taskRow: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  taskNumber: {
    width: 30,
    marginRight: 10,
    fontSize: 12,
    fontWeight: 'bold',
    color: 'white',
  },
  taskName: {
    flex: 1,
    fontSize: 12,
    fontWeight: 'bold',
    color: 'white',
  },
  taskStatusDropdown: {
    width: 120,
  },
  dropdownButton: {
    backgroundColor: '#DDDDDD',
    padding: 10,
  },
  dropdownMenu: {
    backgroundColor: '#DDDDDD',
    marginTop: 5,
    paddingVertical: 5,
  },
  dropdownItem: {
    paddingHorizontal: 10,
    paddingVertical: 5,
  },
  taskButtons: {
    flexDirection: 'row',
    justifyContent: 'flex-end',
    marginLeft: 'auto',
  },
};

export default TaskManager;
